import axios from 'axios';
import { ref, unref, onMounted, onBeforeUnmount, watchEffect, watch } from 'vue';
// vtk
import '@kitware/vtk.js/Rendering/Profiles/Geometry'; // geometry rendering for WebGL, WebGPU
import '@kitware/vtk.js/Rendering/Profiles/Glyph'; // vtkGlyph3DMapper
import vtkFullScreenRenderWindow from '@kitware/vtk.js/Rendering/Misc/FullScreenRenderWindow';
import vtkActor from '@kitware/vtk.js/Rendering/Core/Actor';
import vtkLookupTable from '@kitware/vtk.js/Common/Core/LookupTable';
import vtkDataArray from '@kitware/vtk.js/Common/Core/DataArray';
import vtkMapper from '@kitware/vtk.js/Rendering/Core/Mapper';
import vtkXMLPolyDataReader from '@kitware/vtk.js/IO/XML/XMLPolyDataReader';
import vtkGlyph3DMapper from '@kitware/vtk.js/Rendering/Core/Glyph3DMapper';
import vtkCubeSource from '@kitware/vtk.js/Filters/Sources/CubeSource';
export default {
  setup() {
    const vtkContainer = ref(null);
    const context = ref(null);
    const coneResolution = ref(10);
    const coneOpacity = ref(100);
    const representation = ref(2);
    const textEncoder = new TextEncoder();
    //const path = 'http://localhost:5000';
    const path = 'http://192.168.0.2:5000';
    const node_reader = vtkXMLPolyDataReader.newInstance();
    const node_lut = vtkLookupTable.newInstance();
    const data = new Uint8Array([0, 255, 0, 255, 255, 165, 80, 255, 255, 0, 0, 255]);
    const table = vtkDataArray.newInstance({
      values: data,
      numberOfComponents: 4
    });
    node_lut.setTable(table);
    const node_glyph = vtkCubeSource.newInstance();
    const node_mapper = vtkGlyph3DMapper.newInstance();
    const node_actor = vtkActor.newInstance();
    //
    node_mapper.setInputConnection(node_reader.getOutputPort());
    node_mapper.setScalarModeToUsePointData();
    node_mapper.setColorByArrayName('alert');
    node_mapper.setScalarVisibility(true);
    node_mapper.setScalarRange(1, 3);
    node_mapper.setLookupTable(node_lut);
    //node_mapper.setColorByArrayName("Scalars");
    node_mapper.setColorModeToMapScalars();
    node_mapper.setSourceConnection(node_glyph.getOutputPort());
    node_mapper.setScaleFactor(10.0);
    //node_mapper.setScaleModeToScaleByConstant
    node_actor.setMapper(node_mapper);
    const plan_reader = vtkXMLPolyDataReader.newInstance();
    const plan_mapper = vtkMapper.newInstance();
    const plan_actor = vtkActor.newInstance();
    //
    plan_mapper.setInputConnection(plan_reader.getOutputPort());
    plan_actor.setMapper(plan_mapper);
    plan_actor.getProperty().setOpacity(0.25);
    function setConeResolution(res) {
      coneResolution.value = Number(res);
    }
    function setConeOpacity(opacity) {
      coneOpacity.value = Number(opacity);
    }
    function setRepresentation(rep) {
      representation.value = Number(rep);
    }
    function setConeAtResolution(resolution, reset = false) {
      if (context.value) {
        const {
          renderWindow,
          renderer
        } = context.value;
        axios.post(path + '/resolution', {
          resolution: resolution
        }).then(function (response_post) {
          axios.get(path + '/nodes').then(response_get => {
            reader.parseAsArrayBuffer(textEncoder.encode(response_get.data));
            if (reset) {
              renderer.resetCamera();
              console.log('reset');
            }
            renderWindow.render();
            console.log(response_get.data);
          }).catch(error => {
            console.error(error);
          });
          console.log(response_post);
        }).catch(function (error) {
          console.log(error);
        });
        renderWindow.render();
      }
    }
    function getNodes(reset = false) {
      const {
        renderWindow,
        renderer
      } = context.value;
      axios.get(path + '/nodes').then(response_get => {
        reader.parseAsArrayBuffer(textEncoder.encode(response_get.data));
        if (reset) {
          renderer.resetCamera();
        }
        renderWindow.render();
        console.log(response_get.data);
      }).catch(error => {
        console.error(error);
      });
    }
    function getPlan(reset = false) {
      const {
        renderWindow,
        renderer
      } = context.value;
      axios.get(path + '/plan').then(response_get => {
        plan_reader.parseAsArrayBuffer(textEncoder.encode(response_get.data));
        if (reset) {
          renderer.resetCamera();
        }
        renderWindow.render();
        console.log(response_get.data);
      }).catch(error => {
        console.error(error);
      });
    }
    watch(coneResolution, newValue => {
      setConeAtResolution(newValue);
    });
    watchEffect(() => {
      const opacity = unref(coneOpacity);
      const rep = unref(representation);
      if (context.value) {
        const {
          actor,
          renderWindow
        } = context.value;
        plan_actor.getProperty().setOpacity(opacity * 0.01);
        plan_actor.getProperty().setRepresentation(rep);
        renderWindow.render();
      }
    });
    onMounted(() => {
      if (!context.value) {
        const fullScreenRenderer = vtkFullScreenRenderWindow.newInstance({
          rootContainer: vtkContainer.value
        });
        const renderer = fullScreenRenderer.getRenderer();
        const renderWindow = fullScreenRenderer.getRenderWindow();
        renderer.addActor(actor);
        renderer.addActor(plan_actor);
        context.value = {
          fullScreenRenderer,
          renderWindow,
          renderer,
          //
          node_actor,
          node_mapper,
          node_reader,
          node_glyph,
          //
          plan_reader,
          plan_mapper,
          plan_actor
        };

        // renders as well
        getNodes(false);
        getPlan(true);
        //setConeAtResolution(unref(coneResolution), true)
      }
    });
    onBeforeUnmount(() => {
      if (context.value) {
        //const {fullScreenRenderer, actor, mapper } = context.value;
        const {
          renderer,
          renderWindow,
          fullScreenRenderer,
          node_reader,
          node_actor,
          node_mapper,
          plan_reader,
          plan_actor,
          plan_mapper
        } = context.value;
        plan_actor.delete();
        plan_mapper.delete();
        plan_reader.delete();
        node_actor.delete();
        node_mapper.delete();
        node_reader.delete();
        node_glyph.delete();
        renderer.delete();
        renderWindow.delete();
        fullScreenRenderer.delete();
        context.value = null;
      }
    });
    return {
      vtkContainer,
      setRepresentation,
      setConeResolution,
      setConeOpacity,
      coneOpacity,
      coneResolution,
      representation,
      //
      getNodes,
      getPlan
    };
  }
};
