import "core-js/modules/es.array.push.js";
import "core-js/modules/es.array-buffer.detached.js";
import "core-js/modules/es.array-buffer.transfer.js";
import "core-js/modules/es.array-buffer.transfer-to-fixed-length.js";
import "core-js/modules/es.typed-array.to-reversed.js";
import "core-js/modules/es.typed-array.to-sorted.js";
import "core-js/modules/es.typed-array.with.js";
import axios from 'axios';
import { ref, unref, onMounted, onBeforeUnmount, watchEffect, watch } from 'vue';
// vtk
import '@kitware/vtk.js/Rendering/Profiles/Geometry'; // geometry rendering for WebGL, WebGPU
import '@kitware/vtk.js/Rendering/Profiles/Glyph'; // vtkGlyph3DMapper
import '@kitware/vtk.js/Rendering/Profiles/Molecule'; // vtkSphereMapper
import vtkFullScreenRenderWindow from '@kitware/vtk.js/Rendering/Misc/FullScreenRenderWindow';
import vtkActor from '@kitware/vtk.js/Rendering/Core/Actor';
import vtkMapper from '@kitware/vtk.js/Rendering/Core/Mapper';
import vtkXMLPolyDataReader from '@kitware/vtk.js/IO/XML/XMLPolyDataReader';
import vtkGlyph3DMapper from '@kitware/vtk.js/Rendering/Core/Glyph3DMapper';
import vtkCubeSource from '@kitware/vtk.js/Filters/Sources/CubeSource';
//import vtkSphereSource           from '@kitware/vtk.js/Filters/Sources/SphereSource';
import vtkSphereMapper from '@kitware/vtk.js/Rendering/Core/SphereMapper';
//
import vtkPolyData from '@kitware/vtk.js/Common/DataModel/PolyData';
import vtkPoints from '@kitware/vtk.js/Common/Core/Points';
import vtkCellArray from '@kitware/vtk.js/Common/Core/CellArray';
import vtkLookupTable from '@kitware/vtk.js/Common/Core/LookupTable';
import vtkDataArray from '@kitware/vtk.js/Common/Core/DataArray';
export default {
  setup() {
    const session = {
      user: 'malcolm',
      mine: 'Bambanani'
    };
    let nodes = {
      'data': [],
      'meta': {}
    }; // node data - response getNodeData - for table, polydata
    let events = {
      'data': [],
      'meta': {}
    }; // event data - response getEventData - for table, polydata

    let bounds = new Array(6).fill(0.0);
    const vtkContainer = ref(null);
    const context = ref(null);
    const node_representation = ref(0);
    const events_opacity = ref(50);
    const events_timestamp = ref(1640995200);
    const plan_opacity = ref(100);
    const plan_representation = ref(1);
    const textEncoder = new TextEncoder();
    //const path = 'http://localhost:5000';
    const path = 'http://192.168.0.2:5000';

    // nodes
    const node_reader = vtkXMLPolyDataReader.newInstance();
    const node_lut = vtkLookupTable.newInstance(); // make custom LUT
    const data = new Uint8Array([0, 255, 0, 255, 255, 165, 80, 255, 255, 0, 0, 255]); // green, orange, red
    const table = vtkDataArray.newInstance({
      values: data,
      numberOfComponents: 4
    }); // to VTK
    node_lut.setTable(table); // LUT = green, orange, red

    const node_glyph = vtkCubeSource.newInstance();
    const node_mapper = vtkGlyph3DMapper.newInstance();
    const node_actor = vtkActor.newInstance();
    //
    node_mapper.setScalarModeToUsePointData();
    node_mapper.setScalarVisibility(true);
    node_mapper.setScalarRange(1, 3);
    node_mapper.setLookupTable(node_lut);
    node_mapper.setScaleFactor(10.0);
    node_actor.setMapper(node_mapper);
    //node_actor.getProperty().setAmbient(0.5)

    // plan
    const plan_reader = vtkXMLPolyDataReader.newInstance();
    const plan_mapper = vtkMapper.newInstance();
    const plan_actor = vtkActor.newInstance();
    //
    plan_mapper.setInputConnection(plan_reader.getOutputPort());
    plan_actor.setMapper(plan_mapper);
    plan_actor.getProperty().setOpacity(0.25);
    plan_actor.getProperty().setColor(0.5, 0.5, 0.5);

    // events
    const events_reader = vtkXMLPolyDataReader.newInstance();
    const events_mapper = vtkSphereMapper.newInstance();
    const events_actor = vtkActor.newInstance();
    //
    events_mapper.setInputConnection(events_reader.getOutputPort());
    events_mapper.setScaleFactor(100.0);
    events_mapper.setScaleArray('scale');
    events_mapper.setScalarVisibility(true);
    events_mapper.setScalarModeToUsePointData();
    //events_mapper.setColorByArrayName('dtEvent');
    events_mapper.setScalarRange(1640995200.0, 1656653056.0);
    events_actor.setMapper(events_mapper);
    events_actor.getProperty().setOpacity(0.5);
    function setNodeRepresentation(rep) {
      node_representation.value = Number(rep);
    }
    function setEventOpacity(opacity) {
      events_opacity.value = Number(opacity);
    }
    function setEventTimeStamp(res) {
      events_timestamp.value = Number(res);
    }
    function setPlanOpacity(opacity) {
      plan_opacity.value = Number(opacity);
    }
    function setPlanRepresentation(rep) {
      plan_representation.value = Number(rep);
    }
    async function onEventFilter(timestamp) {
      const {
        renderWindow
      } = context.value;
      await getEvents({
        bounds: bounds,
        mine: session.mine,
        timestamp: timestamp
      });
      renderWindow.render();
    }

    // provide data to vtk pipelines

    function getNodes(args = {}) {
      return new Promise((resolve, reject) => {
        axios.put(path + '/nodes', args).then(response_get => {
          node_reader.parseAsArrayBuffer(textEncoder.encode(response_get.data));
          resolve('nodes');
        }).catch(error => {
          reject(error);
        });
      });
    }
    function getNodeData(args = {}) {
      return new Promise((resolve, reject) => {
        axios.put(path + '/nodes_data', args).then(response_get => {
          nodes = response_get.data;
          const nodes_data = nodes['data'];
          const points = vtkPoints.newInstance();
          points.setNumberOfPoints(nodes_data.length);
          const arr_alerts = new Float32Array(nodes_data.length);
          const arr_verts = [];
          nodes_data.forEach((node, index) => {
            arr_alerts[index] = node.alert;
            arr_verts.push(1, index);
            points.setPoint(index, node.x, node.y, node.z);
          });
          const alerts = vtkDataArray.newInstance({
            numberOfComponents: 1,
            values: arr_alerts,
            name: 'alerts'
          });
          const verts = vtkCellArray.newInstance({
            values: Uint16Array.from(arr_verts)
          });
          const pd = vtkPolyData.newInstance();
          pd.setPoints(points);
          pd.setVerts(verts);
          pd.getPointData().setScalars(alerts);
          node_mapper.setInputData(pd, 0);
          node_mapper.setInputData(node_glyph.getOutputData(), 1);
          resolve('nodes_data');
        }).catch(error => {
          reject(error);
        });
      });
    }
    function getPlan(args = {}) {
      return new Promise((resolve, reject) => {
        axios.put(path + '/plan', args).then(response_get => {
          plan_reader.parseAsArrayBuffer(textEncoder.encode(response_get.data));
          resolve('plan');
        }).catch(error => {
          reject(error);
        });
      });
    }
    function getEvents(args = {}) {
      return new Promise((resolve, reject) => {
        axios.put(path + '/events', args).then(response_get => {
          events_reader.parseAsArrayBuffer(textEncoder.encode(response_get.data));
          events_mapper.setInputData(events_reader.getOutputData());
          resolve('events');
        }).catch(error => {
          reject(error);
        });
      });
    }
    function getEventData(args = {}) {
      return new Promise((resolve, reject) => {
        axios.put(path + '/events_data', args).then(response_get => {
          events = response_get.data;
          const events_data = events['data'];
          const points = vtkPoints.newInstance();
          points.setNumberOfPoints(events_data.length);
          const arr_mags = new Float32Array(events_data.length);
          const arr_verts = [];
          events_data.forEach((event_data, index) => {
            arr_mags[index] = event_data.mag + 2.5;
            arr_verts.push(1, index);
            points.setPoint(index, event_data.x, event_data.y, event_data.z);
          });
          const mags = vtkDataArray.newInstance({
            numberOfComponents: 1,
            values: arr_mags,
            name: 'mags'
          });
          const verts = vtkCellArray.newInstance({
            values: Uint16Array.from(arr_verts)
          });
          const pd = vtkPolyData.newInstance();
          pd.setPoints(points);
          pd.setVerts(verts);
          pd.getPointData().setScalars(mags);
          pd.modified();
          events_mapper.setInputData(pd);
          console.log('getEventData');
          console.log(pd);
          resolve('events_data');
        }).catch(error => {
          reject(error);
        });
      });
    }
    async function getAllAndRender() {
      const {
        renderWindow,
        renderer
      } = context.value;
      await getNodeData({
        mine: session.mine
      });
      bounds = node_mapper.getInputData().getBounds();
      //await getNodes({mine: session.mine})
      //bounds = node_reader.getOutputData().getBounds()
      const buffer = 1000.0;
      bounds[0] -= buffer;
      bounds[1] += buffer;
      bounds[2] -= buffer;
      bounds[3] += buffer;
      bounds[4] -= buffer;
      bounds[5] += buffer;
      //
      await getEventData({
        mine: session.mine
      }); //({bounds: bounds, mine: session.mine, timestamp: 0.0})
      Promise.allSettled([getPlan({
        bounds: bounds,
        mine: session.mine
      }), getEvents({
        bounds: bounds,
        mine: session.mine,
        timestamp: 0.0
      })]).then(() => {
        renderer.resetCamera();
        renderWindow.render();
      });
    }
    function checkModified() {
      return new Promise((resolve, reject) => {
        axios.put(path + '/modified', {
          mine: session.mine
        }).then(response_get => {
          console.log(response_get.data);
          resolve('plan');
        }).catch(error => {
          reject(error);
        });
      });
    }
    watch(events_timestamp, newValue => {
      onEventFilter(newValue);
    });
    watchEffect(() => {
      if (context.value) {
        const {
          renderWindow
        } = context.value;
        console.log(unref(node_representation));
        node_mapper.setScaleMode(unref(node_representation));
        node_mapper.update();
        plan_actor.getProperty().setOpacity(unref(plan_opacity) * 0.01);
        plan_actor.getProperty().setRepresentation(unref(plan_representation));
        events_actor.getProperty().setOpacity(unref(events_opacity) * 0.01);
        renderWindow.render();
      }
    });
    onMounted(() => {
      console.log('onMounted');
      if (!context.value) {
        const fullScreenRenderer = vtkFullScreenRenderWindow.newInstance({
          rootContainer: vtkContainer.value
        });
        const renderer = fullScreenRenderer.getRenderer();
        const renderWindow = fullScreenRenderer.getRenderWindow();
        //
        renderer.setBackground(1.0, 1.0, 1.0);
        renderer.setUseDepthPeeling(true); // depth peeling
        renderer.setMaximumNumberOfPeels(100); // depth peeling
        renderer.setOcclusionRatio(0.01); // depth peeling
        //
        renderer.addActor(node_actor);
        renderer.addActor(plan_actor);
        renderer.addActor(events_actor);
        context.value = {
          fullScreenRenderer,
          renderWindow,
          renderer,
          //
          node_actor,
          node_mapper,
          node_reader,
          node_glyph,
          //
          events_actor,
          events_mapper,
          events_reader,
          //
          plan_reader,
          plan_mapper,
          plan_actor
        };

        // renders as well
        getAllAndRender();
        setInterval(checkModified, 30000);
      }
    });
    onBeforeUnmount(() => {
      if (context.value) {
        //const {fullScreenRenderer, actor, mapper } = context.value;
        const {
          renderer,
          renderWindow,
          fullScreenRenderer,
          node_reader,
          node_actor,
          node_mapper,
          node_glyph,
          plan_reader,
          plan_actor,
          plan_mapper,
          events_reader,
          events_actor,
          events_mapper,
          events_glyph
        } = context.value;
        plan_actor.delete();
        plan_mapper.delete();
        plan_reader.delete();
        node_actor.delete();
        node_mapper.delete();
        node_reader.delete();
        node_glyph.delete();
        events_reader.delete();
        events_actor.delete();
        events_mapper.delete();
        events_glyph.delete();
        //
        renderer.delete();
        renderWindow.delete();
        //
        fullScreenRenderer.delete();
        context.value = null;
      }
    });
    return {
      vtkContainer,
      setNodeRepresentation,
      setEventOpacity,
      setEventTimeStamp,
      setPlanOpacity,
      setPlanRepresentation,
      plan_opacity,
      plan_representation,
      events_timestamp,
      events_opacity,
      node_representation,
      //
      getNodes,
      getNodeData,
      getPlan,
      getEvents,
      getEventData
    };
  }
};
