import axios from 'axios';
import { ref, unref, onMounted, onBeforeUnmount, watchEffect } from 'vue';
import '@kitware/vtk.js/Rendering/Profiles/Geometry';
import vtkFullScreenRenderWindow from '@kitware/vtk.js/Rendering/Misc/FullScreenRenderWindow';
import vtkActor from '@kitware/vtk.js/Rendering/Core/Actor';
import vtkMapper from '@kitware/vtk.js/Rendering/Core/Mapper';
import vtkXMLPolyDataReader from '@kitware/vtk.js/IO/XML/XMLPolyDataReader';
export default {
  setup() {
    const vtkContainer = ref(null);
    const context = ref(null);
    const coneResolution = ref(6);
    const representation = ref(2);
    const reader = vtkXMLPolyDataReader.newInstance();
    const textEncoder = new TextEncoder();
    const path = 'http://localhost:5000/cone';
    axios.get(path).then(res => {
      //reader.ParseXML(res.data);
      reader.parseAsArrayBuffer(textEncoder.encode(res.data));
      console.log(res.data);
    }).catch(error => {
      console.error(error);
    });

    //reader.setUrl('http://localhost:5000/cone')
    //reader.loadData()

    const mapper = vtkMapper.newInstance();
    mapper.setInputConnection(reader.getOutputPort());
    const actor = vtkActor.newInstance();
    actor.setMapper(mapper);
    function setConeResolution(res) {
      coneResolution.value = Number(res);
    }
    function setRepresentation(rep) {
      representation.value = Number(rep);
    }
    watchEffect(() => {
      const res = unref(coneResolution);
      const rep = unref(representation);
      if (context.value) {
        const {
          actor,
          renderWindow
        } = context.value;
        axios.post('http://localhost:5000/resolution', {
          resolution: res
        }).then(function (response) {
          reader.parseAsArrayBuffer(textEncoder.encode(response));
          console.log(res.data);
          console.log(response);
        }).catch(function (error) {
          console.log(error);
        });
        actor.getProperty().setRepresentation(rep);
        renderWindow.render();
      }
    });
    onMounted(() => {
      if (!context.value) {
        const fullScreenRenderer = vtkFullScreenRenderWindow.newInstance({
          rootContainer: vtkContainer.value
        });

        /*
        const reader = vtkXMLPolyDataReader.newInstance();

              const textEncoder = new TextEncoder();
        const path = 'http://localhost:5000/cone';
              axios.get(path)
                .then((res) => {
                  //reader.ParseXML(res.data);
                  reader.parseAsArrayBuffer(textEncoder.encode(res.data));
                  console.log(res.data)
                })
                .catch((error) => {
                  console.error(error);
                });

        //reader.setUrl('http://localhost:5000/cone')
        //reader.loadData()
               const mapper = vtkMapper.newInstance();
        mapper.setInputConnection(reader.getOutputPort());
               const actor = vtkActor.newInstance();
              actor.setMapper(mapper);
        */

        const renderer = fullScreenRenderer.getRenderer();
        const renderWindow = fullScreenRenderer.getRenderWindow();
        renderer.addActor(actor);
        renderer.resetCamera();
        renderWindow.render();
        context.value = {
          fullScreenRenderer,
          renderWindow,
          renderer,
          reader,
          //coneSource,
          actor,
          mapper
        };
      }
    });
    onBeforeUnmount(() => {
      if (context.value) {
        const {
          fullScreenRenderer,
          coneSource,
          actor,
          mapper
        } = context.value;
        //const { renderer, renderWindow, fullScreenRenderer, coneSource, actor, mapper } = context.value;
        actor.delete();
        mapper.delete();
        coneSource.delete();

        //renderer.delete()
        //renderWindow.delete()

        fullScreenRenderer.delete();
        context.value = null;
      }
    });
    return {
      vtkContainer,
      setRepresentation,
      setConeResolution,
      coneResolution,
      representation
    };
  }
};
